pragma solidity ^0.5.0;

import "./Permissioned.sol";
import "openzeppelin-solidity/contracts/lifecycle/Pausable.sol";

contract GGPoint is Permissioned, Pausable {
    //Byte32 mappings are done using some hash of identifiable learner information
    //(memberID, publicKey, publicKey+memberId etc.)
    //This allows us to use non-ECDSA keys and not have to use addresses to tie data to learners
    mapping (address => uint256) private points;// learner specific point amount.
    mapping (address => bytes32[]) private transcripts;//learner specific transcript array.
    mapping (address => mapping(bytes32 => bool)) private learnedSkills;//learner specific hashmap of skills learned.

    constructor () public {}
    //Takes a trimmed IPFS hash (w/o first two hex bytes(sha function version and bit size of function))
    //First byte is usually 0x12 (Sha2)
    //Second byte is usually 0x20 (256 bit)
    //can only be performed by a trusted sender
    function addTranscript(address transcriptOwner, bytes32 transcript) public onlyTrusted(msg.sender) whenNotPaused() returns (bool success) {
        transcripts[transcriptOwner].push(transcript);
        return true;
    }
    //Returns a specific transcript(trimmed 0x1220) tied to a learner.
    function getTranscript(address transcriptOwner, uint index) public view onlyMatching(msg.sender, transcriptOwner) returns (bytes32 hashe) {
        return transcripts[transcriptOwner][index];
    }
    //Returns all transcripts(trimmed 0x1220) tied to a learner.
    function getTranscripts(address transcriptOwner) public view onlyMatching(msg.sender, transcriptOwner) returns (bytes32[] memory hashes) {
        return transcripts[transcriptOwner];
    }
    //Overwrites the transcriptOwners transcript at the given index with the provided array
    function setTranscript(address transcriptOwner, bytes32 newTranscript, uint index) public onlyOfficers(msg.sender) returns(bool success){
        transcripts[transcriptOwner][index] = newTranscript;
        return true;
    }
    //Overwrites the transcriptOwners transcripts with the provided array
    function setTranscripts(address transcriptOwner, bytes32[] memory newTranscripts) public onlyOfficers(msg.sender) returns(bool success){
        transcripts[transcriptOwner] = newTranscripts;
        return true;
    }
    //removes a skill if it was falsely flagged as learnt
    function removeLearnedSkill(address learner, bytes32 skill) public onlyTrusted(msg.sender) returns (bool success) {
        learnedSkills[learner][skill] = false;
        return true;
    }
    //Adds a skill hash learned by a learner, can only be performed by a trusted sender, can only be performed by a trusted sender
    function addLearnedSkill(address learner, bytes32 skill) public onlyTrusted(msg.sender) whenNotPaused() returns (bool success) {
        learnedSkills[learner][skill] = true;
        return true;
    }
    //Helper function to determine if a learner is eligible for a payout tied to a skill hash
    function hasLearnedSkill(address learner, bytes32 Skill) public view onlyTrusted(msg.sender) returns (bool Learned) {
        bool hasLearned = learnedSkills[learner][Skill] || false;
        return hasLearned;
    }

    //Payout function that takes a learnerID, amount of points, and the skill hash associated with it.
    function payoutLearner( address learner, uint256 amount, bytes32 skill, bytes32 transcript ) public onlyTrusted(msg.sender) whenNotPaused() returns (bool success){
        require(hasLearnedSkill(learner, skill) == false, "Learner has already learned this skill");
        //update skill hashmap and give the learner some points
        addLearnedSkill(learner, skill);
        addTranscript(learner, transcript);
        points[learner] += amount;

        return true;
    }
    //Returns point balance that a specific learner has
    function balanceOf (address learner) public view returns (uint256 balance){
        return points[learner];
    }

    function pause() public onlyOwner(msg.sender) {
        return super.pause();
    }

    function unpause() public onlyOwner(msg.sender) {
        return super.unpause();
    }

}
